import React, { useState, useEffect } from 'react';

    const initialForm={
        name:"",
        constellation:"",
        id:null,
    }

const CrudForm = ({createData,updateData,dataToEdit,setDataToEdit}) => {
    const [form, setForm] = useState(initialForm)

    useEffect(() => {
      if (dataToEdit){
          setForm(dataToEdit);
      }
      else{
          setForm(initialForm);
      }
    }, [dataToEdit])
    

    const handleChange = (e) => {
        setForm({
          ...form,
          [e.target.name]: e.target.value,
        });
      };

  
  const handleSubmit=(e)=>{
      //to avoid processing
      e.preventDefault();
      if(!form.constellation || !form.name){
        alert("Datos Incompletos")
            return}

      if(form.id === null){
          createData(form);
               }
      else{
          updateData(form);
      }
      handleReset();
  }
  const handleReset=(e)=>{
    setForm(initialForm);
    setDataToEdit(null);
}
  
    return (     
        <div>
            {(setDataToEdit)?
            <h3>Editar</h3>
            :<h3>Editar</h3>
}
   <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Nombre Caballero" onChange={handleChange} value={form.name}></input>
        <input type="text" name="constellation" placeholder="Constelacion" onChange={handleChange} value={form.constellation}></input>
        <input type="submit" value="Enviar" ></input>
        <input type="reset" value="Limpiar" onClick={handleReset}></input>
    </form>
    </div>
  )
}

export default CrudForm
