import React, { useState, useEffect } from 'react';

    const initialForm={
        name:"",
        constellation:"",
        id:null,
    }

const CrudForm = ({createData,updateData}) => {
    const [form, setForm] = useState({})

  const handleChange=(e)=>{
    setForm({
        ...form,
        [e.target.name]:e.target.value
    })
  }

  
  const handleSubmit=(e)=>{
      e.preventDefault();
      if(!form.id){
          createData(form);
      }
      else{
          updateData(form);
      }
      
  }
  const handleReset=(e)=>{

}
  
    return (     
        <div>
            <h3>Agregar</h3>
   <form onSubmit={handleSubmit}>
        <input type="text" name="nombre" placeholder="Nombre Caballero" onChange={handleChange} value={form.name}></input>
        <input type="text" name="constellation" placeholder="Constelacion" onChange={handleChange} value={form.constellation}></input>
        <input type="submit" value="Enviar" onClick={handleSubmit}></input>
        <input type="reset" value="Limpiar" onClick={handleReset}></input>


    </form>
    </div>
  )
}

export default CrudForm
